import React, { useState, useEffect, useRef } from 'react';
import MessageInput from './MessageInput';
import OnlineUsersList from './OnlineUsersList';
import TypingIndicator from './TypingIndicator';

const ChatRoom = ({ socket, room, username, onLeaveRoom }) => {
  const [messages, setMessages] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const [typingUser, setTypingUser] = useState('');
  const messagesEndRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  // Auto-scroll to bottom when new messages arrive
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // Set up Socket.io event listeners
  useEffect(() => {
    if (!socket) return;

    // Load chat history when joining room
    socket.on('loadHistory', (history) => {
      const formattedMessages = history.map(msg => ({
        id: msg._id,
        username: msg.username,
        message: msg.message,
        timestamp: new Date(msg.timestamp),
        isSystem: false
      }));
      setMessages(formattedMessages);
    });

    // Receive new messages
    socket.on('chatMessage', (data) => {
      setMessages(prev => [...prev, {
        id: Date.now(),
        username: data.username,
        message: data.message,
        timestamp: new Date(data.timestamp),
        isSystem: false
      }]);
    });

    // System messages (user joined/left)
    socket.on('systemMessage', (data) => {
      setMessages(prev => [...prev, {
        id: Date.now(),
        message: data.message,
        timestamp: new Date(data.timestamp),
        isSystem: true
      }]);
    });

    // Update online users list
    socket.on('onlineUsers', (data) => {
      setOnlineUsers(data.usernames || []);
    });

    // Show typing indicator
    socket.on('typing', (data) => {
      setTypingUser(data.username);
      setIsTyping(true);
      
      // Clear previous timeout
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }
      
      // Auto-stop typing after 3 seconds
      typingTimeoutRef.current = setTimeout(() => {
        setIsTyping(false);
      }, 3000);
    });

    // Hide typing indicator
    socket.on('stopTyping', () => {
      setIsTyping(false);
      if (typingTimeoutRef.current) {
        clearTimeout(typingTimeoutRef.current);
      }
    });

    return () => {
      socket.off('loadHistory');
      socket.off('chatMessage');
      socket.off('systemMessage');
      socket.off('onlineUsers');
      socket.off('typing');
      socket.off('stopTyping');
    };
  }, [socket]);

  const handleSendMessage = (message) => {
    if (socket && message.trim()) {
      socket.emit('chatMessage', { message, room, username });
      socket.emit('stopTyping', { room });
    }
  };

  const handleTyping = () => {
    if (socket) {
      socket.emit('typing', { room, username });
    }
  };

  const formatTime = (date) => {
    const d = new Date(date);
    return d.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="chat-room">
      {/* Header */}
      <div className="chat-header">
        <div className="header-content">
          <h2>üìÅ {room}</h2>
          <button onClick={onLeaveRoom} className="leave-btn">Leave Room</button>
        </div>
      </div>

      {/* Main Chat Container */}
      <div className="chat-container">
        {/* Messages Section */}
        <div className="messages-section">
          <div className="messages">
            {messages.length === 0 ? (
              <div className="empty-state">
                <p>üëã Welcome to {room}!</p>
                <p>Start the conversation...</p>
              </div>
            ) : (
              messages.map((msg) => (
                <div 
                  key={msg.id} 
                  className={`message ${msg.isSystem ? 'system-message' : ''}`}
                >
                  {!msg.isSystem && (
                    <div className="message-header">
                      <span className="username">üë§ {msg.username}</span>
                      <span className="timestamp">{formatTime(msg.timestamp)}</span>
                    </div>
                  )}
                  <div className={`message-text ${msg.isSystem ? 'system-text' : ''}`}>
                    {msg.isSystem ? `‚ÑπÔ∏è ${msg.message}` : msg.message}
                  </div>
                </div>
              ))
            )}
            {isTyping && <TypingIndicator username={typingUser} />}
            <div ref={messagesEndRef} />
          </div>

          {/* Message Input */}
          <MessageInput onSendMessage={handleSendMessage} onTyping={handleTyping} />
        </div>

        {/* Online Users Sidebar */}
        <OnlineUsersList users={onlineUsers} currentUser={username} />
      </div>
    </div>
  );
};

export default ChatRoom;
