import React, { useState } from 'react';

const RoomSelection = ({ onJoinRoom }) => {
  const [roomName, setRoomName] = useState('');
  const [username, setUsername] = useState('Guest');

  const handleJoin = () => {
    if (roomName.trim()) {
      onJoinRoom(roomName, username || 'Guest');
      setRoomName('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleJoin();
    }
  };

  return (
    <div className="room-selection">
      <div className="room-selection-container">
        <h1>Real-Time Chat</h1>
        <p>Join or create a chat room</p>
        
        <div className="input-group">
          <label>Username:</label>
          <input
            type="text"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            placeholder="Enter your name (default: Guest)"
            onKeyPress={handleKeyPress}
          />
        </div>

        <div className="input-group">
          <label>Room Name:</label>
          <input
            type="text"
            value={roomName}
            onChange={(e) => setRoomName(e.target.value)}
            placeholder="Enter room name"
            onKeyPress={handleKeyPress}
          />
        </div>

        <button onClick={handleJoin} className="join-btn">
          Join Room
        </button>

        <div className="info">
          <p>ðŸ’¡ Tip: Create a new room or join an existing one using the same room name</p>
        </div>
      </div>
    </div>
  );
};

export default RoomSelection;
