import React, { useState, useEffect, useRef } from 'react';
import MessageInput from './MessageInput';
import OnlineUsersList from './OnlineUsersList';
import TypingIndicator from './TypingIndicator';

const ChatRoom = ({ socket, room, username, onLeaveRoom }) => {
  const [messages, setMessages] = useState([]);
  const [onlineUsers, setOnlineUsers] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const messagesEndRef = useRef(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  useEffect(() => {
    if (!socket) return;

    // Load chat history
    socket.on('loadHistory', (history) => {
      setMessages(history.map(msg => ({
        id: msg._id,
        username: msg.username,
        message: msg.message,
        timestamp: new Date(msg.timestamp)
      })));
    });

    // Receive new messages
    socket.on('chatMessage', (data) => {
      setMessages(prev => [...prev, {
        id: Date.now(),
        username: data.username,
        message: data.message,
        timestamp: new Date(data.timestamp)
      }]);
    });

    // Update online users
    socket.on('onlineUsers', (data) => {
      setOnlineUsers(data.usernames || []);
    });

    // Typing indicator
    socket.on('typing', (data) => {
      setIsTyping(data.username);
    });

    socket.on('stopTyping', () => {
      setIsTyping(false);
    });

    return () => {
      socket.off('loadHistory');
      socket.off('chatMessage');
      socket.off('onlineUsers');
      socket.off('typing');
      socket.off('stopTyping');
    };
  }, [socket]);

  const handleSendMessage = (message) => {
    if (socket && message.trim()) {
      socket.emit('chatMessage', { message, room, username });
      socket.emit('stopTyping', { room });
    }
  };

  const handleTyping = () => {
    if (socket) {
      socket.emit('typing', { room, username });
    }
  };

  const formatTime = (date) => {
    return new Date(date).toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' });
  };

  return (
    <div className="chat-room">
      <div className="chat-header">
        <div className="header-content">
          <h2>Room: {room}</h2>
          <button onClick={onLeaveRoom} className="leave-btn">Leave Room</button>
        </div>
      </div>

      <div className="chat-container">
        <div className="messages-section">
          <div className="messages">
            {messages.length === 0 ? (
              <div className="empty-state">
                <p>Welcome to {room}! Start the conversation.</p>
              </div>
            ) : (
              messages.map((msg) => (
                <div key={msg.id} className="message">
                  <div className="message-header">
                    <span className="username">{msg.username}</span>
                    <span className="timestamp">{formatTime(msg.timestamp)}</span>
                  </div>
                  <div className="message-text">{msg.message}</div>
                </div>
              ))
            )}
            {isTyping && <TypingIndicator username={isTyping} />}
            <div ref={messagesEndRef} />
          </div>

          <MessageInput onSendMessage={handleSendMessage} onTyping={handleTyping} />
        </div>

        <OnlineUsersList users={onlineUsers} currentUser={username} />
      </div>
    </div>
  );
};

export default ChatRoom;
